mapper.select:
SELECT A.*, M.name AS extra__writer FROM article AS A LEFT JOIN member AS M ON A.memberId = M.id LEFT JOIN board AS B ON A.boardId = B.id WHERE B.code = #{boardCode}
AND A.title LIKE CONCAT('%',#{searchKeyword},'%')
AND A.body LIKE CONCAT('%',#{searchKeyword},'%')
AND (A.title LIKE CONCAT('%',#{searchKeyword},'%') OR A.body LIKE CONCAT('%',#{searchKeyword},'%'))
ORDER BY A.id DESC
LIMIT #{limitFrom}, #{limitTake}

mapper.update:
UPDATE article
SET hit = hit + 1
WHERE id = #{id};
